          <NativeSelectOption value="@hotmail.com">
            @hotmail.com
          </NativeSelectOption>
        </NativeSelect>
      </ControlGroupItem>
      <ControlGroupItem>
        <Button>Subscribe</Button>
      </ControlGroupItem>
    </ControlGroup>
  );
}
