import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import {
  InputBase,
  InputBaseAdornment,
  InputBaseAdornmentButton,
  InputBaseControl,
  InputBaseInput,
} from "@/components/ui/input-base";

export function InputBaseWithAction() {
  return (
    <InputBase>
      <InputBaseAdornment>
        <Mail />
      </InputBaseAdornment>
      <InputBaseControl>
        <InputBaseInput type="email" placeholder="Email" />
      </InputBaseControl>
      <TooltipProvider>
        <Tooltip>
          <InputBaseAdornment>
            <InputBaseAdornmentButton asChild>
              <TooltipTrigger>
                <Info />
              </TooltipTrigger>
            </InputBaseAdornmentButton>
          </InputBaseAdornment>
          <TooltipContent>
            <p>Email must be unique.</p>
          </TooltipContent>
        </Tooltip>
      </TooltipProvider>
    </InputBase>
  );
}
