import { Card, CardContent } from "@/components/ui/card";
import { cn } from "@/lib/utils";
import { VirtualizedList } from "@/components/ui/virtualized";

const items = Array.from({ length: 10000 }, (_, index) => index);

export function VirtualizerDemo() {
  return (
    <div className="size-80">
      <VirtualizedList>
        {items.map((item, index) => (
          <Card
            key={item}
            className={cn("mb-4", index === items.length - 1 && "mb-0")}
          >
            <CardContent className="flex aspect-square items-center justify-center p-6">
              <span className="text-4xl font-semibold">{item}</span>
            </CardContent>
          </Card>
        ))}
      </VirtualizedList>
    </div>
  );
