              </SortableItemTrigger>
            </SortableItem>
          ))}
        </VirtualizedList>
      </SortableList>
      <SortableOverlay>
        {(activeId) => {
          const activeItem = items.find((item) => item.id === activeId);
          if (!activeItem) {
            return null;
          }

          return (
            <Item
              title={activeItem.title}
              description={activeItem.description}
              className="cursor-grabbing shadow-lg"
            />
          );
        }}
      </SortableOverlay>
    </Sortable>
